 package codebro;

public class Ifstatement7 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		// if statement = performs a block of code if it's condition evaluates to be true
	int age = 28;
	
	if(age>=28) {
		System.out.println("You should get married faster");
	}
		else if (age<28) {
			System.out.println("You still have to plan for marriage");
			
		}
		else { System.out.println("take your time don't rush it");
		
	}
	}

}
